CREATE TABLE BETEGEK(BTAJSZAM VARCHAR(11) PRIMARY KEY,NEV VARCHAR(30),SZEMIGZ VARCHAR(8),VERCSOPORT VARCHAR(3),ISZ INTEGER,VAROS VARCHAR(20),UTCA VARCHAR(30),HSZ INTEGER,BETEGSEG VARCHAR(40));
CREATE TABLE KORHAZ (KKOD VARCHAR(10) PRIMARY KEY,NEV VARCHAR(30),VAROS VARCHAR(20),ISZ INTEGER,UTCA VARCHAR(30),HSZ INTEGER);
CREATE TABLE ASSZISZTENS (ASZEMIGZ VARCHAR(8) PRIMARY KEY,NEV VARCHAR(30),BEOSZTAS VARCHAR(20),TELSZAM VARCHAR(15),ISZ INTEGER,VAROS VARCHAR(20),UTCA VARCHAR(30),HSZ INTEGER,KKOD VARCHAR(10) REFERENCES KORHAZ(KKOD));
CREATE TABLE VERADO (VTAJSZAM VARCHAR(11) PRIMARY KEY,SZEMIGZ VARCHAR(8),NEV VARCHAR(30),ELETKOR VARCHAR(2),VERCSOPORT VARCHAR(3),ISZ INTEGER,VAROS VARCHAR(20),UTCA VARCHAR(30),HSZ INTEGER,ASZEMIGZ VARCHAR(8) REFERENCES ASSZISZTENS(ASZEMIGZ));
CREATE TABLE VER (VKOD VARCHAR(10) PRIMARY KEY,VERADASIDEJE DATE,VERCSOPORT VARCHAR(3),LEJARAT DATE,VTAJSZAM VARCHAR(11) REFERENCES VERADO(VTAJSZAM),BTAJSZAM VARCHAR(11) REFERENCES BETEGEK(BTAJSZAM));
CREATE TABLE SZOLGALTATAS (KIVIZSGALAS VARCHAR(50) PRIMARY KEY,UTALVANY VARCHAR(20),KKOD VARCHAR(10) REFERENCES KORHAZ(KKOD),VTAJSZAM VARCHAR(11) REFERENCES VERADO(VTAJSZAM));